mapper.select:
select
,
from activity_content where id = #{id,jdbcType=VARCHAR}

mapper.insert:

  SELECT CONCAT(uuid_short(),'')

insert into activity_content (id, activity_id, type, 
  create_user, create_time, update_user, 
  update_time, status, activity_introduce
  )
values (#{id,jdbcType=VARCHAR}, #{activityId,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}, 
  #{createUser,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{status,jdbcType=VARCHAR}, #{activityIntroduce,jdbcType=LONGVARCHAR}
  )

